var each = function(obj, callback, args) {  var value,    i = 0,    length = obj.length,    isArray = isArraylike(obj);    if (isArray) {      for (; i < length; i++) {        value = callback.call(obj[i], i, obj[i]);        if (value === false) {          break;        }      }    } else {      for (i in obj) {        value = callback.call(obj[i], i, obj[i]);        if (value === false) {          break;        }      }    }  return obj;};